sp_widget.client_script:
function() {	
	var c = this;		
  c.display = function() {		
		c.data.message = (c.data.sometext) ? 'Hello ' + c.data.sometext + '!' : '';
	}

	c.display();

	c.data.supportedOperators = ["*", "/", "+", "-"];
	var params = c.options.widget_parameters;
	if (params) {
			params = JSON.parse(params);
			c.data.operator = params.operator;
			c.data.f1 = params.f1;
			c.data.f2 = params.f2;
	 } else {
			c.data.operator = "*";
			c.data.f1 = 10;
			c.data.f2 = "2";		 
	 }


		c.calci = function() {
			switch(c.data.operator) {
				case "*" :
					 c.data.result = parseFloat(c.data.f1) * parseFloat(c.data.f2);
					 break;	
				case "/" :
					 c.data.result = parseFloat(c.data.f1) / parseFloat(c.data.f2);
					 break;	
				case "+" :
					 c.data.result = parseFloat(c.data.f1) + parseFloat(c.data.f2);
					break;
				case "-" :
					 c.data.result = parseFloat(c.data.f1) - parseFloat(c.data.f2);
					 break;	
				default:
					c.data.result = c.data.f1 + "  " + c.data.f2;
			}
		}


}
